import React, { useEffect, useState } from 'react';
import {
  Table,
  TableContainer,
  TableHead,
  TableBody,
  TableRow,
  TableCell,
  Button,
  Typography,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import axios from 'axios';

const useStyles = makeStyles((theme) => ({
  testResults: {
    padding: '20px',
  },
  refreshButton: {
    margin: theme.spacing(2, 0),
  },
}));

const TestResults = () => {
  const classes = useStyles();
  const [data, setData] = useState(null);

  useEffect(() => {
    fetchResults();
    const interval = setInterval(fetchResults, 15000);
    return () => {
      clearInterval(interval);
    };
  }, []);

  const fetchResults = async () => {
    try {
      const response = await axios.post('/calculate_results');
      setData(response.data);
    } catch (error) {
      console.error('Error fetching results:', error);
    }
  };

  const renderMetricsTable = () => {
    if (!data) {
      return null;
    }

    const { metrics, experiment_confidence_interval, status, variants, winner, date_created } = data;

    return (
      <>
        <Typography variant="h6" gutterBottom>
          Experiment: 
        </Typography>
        <Typography variant="subtitle1" gutterBottom>
          Date Created: {date_created}
        </Typography>
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Variants</TableCell>
                <TableCell>Impressions</TableCell>
                <TableCell>Click-Through Rate</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {variants.map((variant) => (
                <TableRow key={variant}>
                  <TableCell>{variant}</TableCell>
                  <TableCell>{metrics[variant].impressions}</TableCell>
                  <TableCell>{metrics[variant].click_through_rate.toFixed(2)}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
        <Typography variant="subtitle1" gutterBottom>
          Confidence Interval: {confidence_interval.toFixed(2)}%
        </Typography>
        <Typography variant="subtitle1" gutterBottom>
          Status: {status}
        </Typography>
        <Typography variant="subtitle1" gutterBottom>
          Winner: {winner}
        </Typography>
      </>
    );
  };

  return (
    <div className={classes.testResults}>
      {renderMetricsTable()}
      <Button
        variant="contained"
        color="primary"
        className={classes.refreshButton}
        onClick={fetchResults}
      >
        Refresh
      </Button>
    </div>
  );
};

export default TestResults;
