import React, { useState } from 'react';
import TestResults from './TestResults';

enum ActiveTab {
  ABTestCreator = 'ABTestCreator',
  TestResults = 'TestResults',
}

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<ActiveTab>(ActiveTab.ABTestCreator);

  const handleTabChange = (tab: ActiveTab) => {
    setActiveTab(tab);
  };

  return (
    <div className="app">
      <div className="tabs">
        <button
          className={`tab-btn ${activeTab === ActiveTab.ABTestCreator ? 'active' : ''}`}
          onClick={() => handleTabChange(ActiveTab.ABTestCreator)}
        >
          Create AB Test
        </button>
        <button
          className={`tab-btn ${activeTab === ActiveTab.TestResults ? 'active' : ''}`}
          onClick={() => handleTabChange(ActiveTab.TestResults)}
        >
          Test Results
        </button>
      </div>
      <div className="tab-content">
        {activeTab === ActiveTab.ABTestCreator && <ABTestCreator />}
        {activeTab === ActiveTab.TestResults && <TestResults />}
      </div>
    </div>
  );
};

export default App;
