import React, { useState } from 'react';
import './App.css';
import CreateTest from './components/CreateTest/CreateTest';
import logo from './assets/winlogo.png';
import CampaignResults from './components/CampaignResults/CapaignResults';

const App: React.FC = () => {

  const [campaignData, setCampaignData] = useState({
    campaignName : '', 
    campaignId: 0,
    controlName: '',
    controlIdentifier: '',
    type: '',
    variants: [
      {
        name: '',
        identifier: '',
      } 
    ]
  });

  const [currentTab, setCurrentTab] = useState('createTest');

  const handleTestSelection = (selectedTestName: string) => {
    setCampaignData(...campaignData, selectedCaName);
    setCurrentTab('testResult');
  };

  return (
       <div className="app">
      <header style={{textAlign: 'left'}} >
        <h1>
        <img src={logo} alt="WinAb logo" style={{height: '100px', width: '100px'}} />  {/* This is where the logo is displayed */}       
           <span>Win</span>
          <span>Ab</span>
        </h1>

      </header>

      <nav>
        <div
          className={`tab ${currentTab === 'createTest' ? 'active' : ''}`}
          onClick={() => setCurrentTab('createTest')}
        >
          Create A Campaign
        </div>
        <div
          className={`tab ${currentTab === 'testResult' ? 'active' : ''}`}
          onClick={() => setCurrentTab('testResult')}
        >
          Campaign Details
        </div>
      </nav>

      <div className="tab-content">
        {currentTab === 'createTest' && <CreateTest setCampaignData={setTestName} />}
        {currentTab === 'testResult' && <CampaignResults />}
      </div>
    </div>
  );
};

export default App;
