import React, { useState } from 'react';
import ABTestService from '../../services/api';
import { styled } from '@mui/material/styles';
import { Button, TextField, Container, Box, Typography } from "@mui/material";

interface CreateTestProps {
  setTestId: React.Dispatch<React.SetStateAction<string | null>>;
}

const FormContainer = styled('form')({
  '& .MuiTextField-root': {
    margin: '8px',
    width: '25ch',
  },
});

const LineInput = styled(TextField)({
  '& .MuiInputBase-root': {
    border: 'none',
    borderBottom: '1px solid rgba(0, 0, 0, 0.42)',
  },
});

const CreateTest: React.FC<CreateTestProps> = ({ setTestId }) => {
  const [formData, setFormData] = useState({
    experiment_name: '',
    original_name: '',
    original_xpath: '',
    element_type: '',
    variants: [
      {
        name: '',
        element: { name: '', xpath: '', headline: '', teaser: '', custom: '' }
      }
    ]
  });

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleVariantChange = (event: React.ChangeEvent<HTMLInputElement>, index: number) => {
    const { name, value } = event.target;
    console.log('Index:', index);
    console.log('Name:', name);
    console.log('Value:', value);
    const updatedVariants = [...formData.variants];
    updatedVariants[index].name = value;
    setFormData((prevData) => ({
      ...prevData,
      variants: updatedVariants
    }));
};



  const handleElementChange = (
    event: React.ChangeEvent<HTMLInputElement>,
    variantIndex: number
  ) => {
    const { name, value } = event.target;
    const updatedVariants = [...formData.variants];
    updatedVariants[variantIndex].element = {
      ...updatedVariants[variantIndex].element,
      [name]: value
    };
    setFormData((prevData) => ({
      ...prevData,
      variants: updatedVariants
    }));
  };

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    try {
      await ABTestService.createTest(formData);
      setTestId(formData.experiment_name);
      alert('Test created successfully');
    } catch (error) {
      console.error('Error creating test:', error);
      alert('Failed to create test');
    }
  };

  return (
    <Container maxWidth="sm">
      <FormContainer noValidate autoComplete="off" onSubmit={handleSubmit}>
        <Box mb={3}>
          <Typography variant="h6" component="h2">
            Set Up Campaign
          </Typography>
  
          <LineInput
            id="experiment_name"
            label="Campaign Name"
            name="experiment_name"
            value={formData.experiment_name}
            onChange={handleInputChange}
            variant="outlined"
            fullWidth
            required
          />
  
          <LineInput
            id="original_name"
            label="Original Element Name"
            name="original_name"
            value={formData.original_name}
            onChange={handleInputChange}
            variant="outlined"
            fullWidth
            required
          />
  
          <LineInput
            id="original_xpath"
            label="Original Element XPath"
            name="original_xpath"
            value={formData.original_xpath}
            onChange={handleInputChange}
            variant="outlined"
            fullWidth
            required
          />
  
          <TextField
            id="element_type"
            select
            label="Type of Test"
            name="element_type"
            value={formData.element_type}
            onChange={handleInputChange}
            SelectProps={{ native: true }}
            variant="outlined"
            fullWidth
          >
            <option value="">Select</option>
            <option value="headline">Headline</option>
            <option value="teaser">Teaser</option>
            <option value="custom">Custom</option>
          </TextField>
  
          <Typography variant="h6" component="h2" sx={{ mt: 2 }}>
            Create Variance
          </Typography>
          {formData.variants.map((variant, variantIndex) => (
  <Box key={variantIndex} mb={3}>
    <TextField
      id={`variant-name-${variantIndex}`}
      label="Variance Name"
      name="name"
      value={variant.name}
      onChange={(event: React.ChangeEvent<HTMLInputElement>) =>
        handleVariantChange(event, variantIndex)
      }
      variant="outlined"
      fullWidth
      required
    />

    {formData.element_type === 'headline' && (
      <LineInput
        id={`element-headline-${variantIndex}`}
        label="Alternate Headline"
        name="headline"
        value={variant.element.name}
        onChange={(event: React.ChangeEvent<HTMLInputElement>) =>
          handleElementChange(event, variantIndex)
        }
        variant="outlined"
        fullWidth
      />
    )}

    {formData.element_type === 'teaser' && (
      <LineInput
        id={`element-teaser-${variantIndex}`}
        label="Alternate Teaser Image"
        name="teaser"
        value={variant.element.name}
        onChange={(event: React.ChangeEvent<HTMLInputElement>) =>
          handleElementChange(event, variantIndex)
        }
        variant="outlined"
        fullWidth
      />
    )}

    {formData.element_type === 'custom' && (
      <LineInput
        id={`element-custom-${variantIndex}`}
        label="Custom"
        name="custom"
        value={variant.element.name}
        onChange={(event: React.ChangeEvent<HTMLInputElement>) =>
          handleElementChange(event, variantIndex)
        }
        variant="outlined"
        fullWidth
      />
    )}
  </Box>
))}

  
          <Button
            variant="outlined"
            color="primary"
            onClick={handleAddVariant}
            sx={{ mt: 2 }}
          >
            Add Variant
          </Button>
        </Box>
  
        <Button variant="contained" color="primary" type="submit">
          Create Test
        </Button>
      </FormContainer>
    </Container>
  );
  };

export default CreateTest;
