import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Table, TableBody, TableCell, TableHead, TableRow, Button } from '@material-ui/core';
import ABTestService from '../../services/api';

interface Metrics {
  [key: string]: {
    views: number;
    clickThroughRate: number;
  };
}

interface ResultData {
  metrics: Metrics;
  confidence_interval: number;
  status: string;
  variants: string[];
  winner: string | null;
  date_created: string;
  
}

const TestResults: React.FC = () => {
  const useStyles = makeStyles((theme) => ({
    testResults: {
      padding: '20px',
    },
    heading: {
      marginBottom: '20px',
    },
    table: {
      width: '100%',
      borderCollapse: 'collapse',
      marginBottom: '20px',
    },
    tableHeaderCell: {
      padding: '10px',
      border: '1px solid #ccc',
      backgroundColor: '#f7f7f7',
      color: '#333',
      fontWeight: 'bold',
    },
    tableCell: {
      padding: '10px',
      border: '1px solid #ccc',
      textAlign: 'center',
    },
    winnerCell: {
      fontWeight: 'bold',
    },
    refreshButton: {
      marginTop: '10px',
    },
    
  balloonEmoji: {
    marginRight: theme.spacing(1),
  },
  }));

  const classes = useStyles();
  const [resultData, setResultData] = useState<ResultData | null>(null);

  const fetchResults = async () => {
    try {
      const response = ABTestService.getTestResults(experiment_name);
      setResultData(response.data);
    } catch (error) {
      console.error('Error fetching results:', error);
    }
  };

  useEffect(() => {
    fetchResults();
    const interval = setInterval(fetchResults, 15000); // Fetch results every 15 seconds
    return () => clearInterval(interval);
  }, []);

  const renderMetricsTable = () => {
    if (!resultData) {
      return null;
    }

    const { metrics, confidence_interval, status, variants, winner, date_created } = resultData;

    return (
      <>
        <h2 className={classes.heading}>Test Results</h2>
        <p>Date Created: {date_created}</p>
        <Table className={classes.table}>
          <TableHead>
            <TableRow>
              <TableCell>Variant</TableCell>
              <TableCell>Impressions</TableCell>
              <TableCell>Click Through Rate</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {variants.map((variant) => (
              <TableRow key={variant}>
                <TableCell>{variant}</TableCell>
                <TableCell>{metrics[variant].views}</TableCell>
                <TableCell>{metrics[variant].clickThroughRate.toFixed(2)}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
        <p>Status: {status}</p>
        <p>Confidence Interval: {confidence_interval.toFixed(2)}</p>
        {winner && (
          <p>
          Winner: {winner ? <span className={classes.balloonEmoji}>🎈</span> : ''}  
                  </p>
        )}
      </>
    );

  };

  return (
    <div className={classes.testResults}>
      {renderMetricsTable()}
      <Button
        variant="contained"
        color="primary"
        className={classes.refreshButton}
        onClick={fetchResults}
      >
        Refresh
      </Button>
    </div>
  );
};
        
      
      export default TestResults;
