// ABTestService.ts
import { CampaignResultResponse, CampaignData } from './types';

const ABTestService = {
 // Replace TestResultResponse with ResultData in the return type of getTestResults
async getAllCampaignResults(): Promise<CampaignResultResponse> {
  try {
    const response = await fetch(`http://localhost:5002/v1/get_results`);
    const data = await response.json();
    console.log('API Response:', data); // Log the response so we can see it in the browser console
    return data;
  } catch (error) {
    console.error('Error fetching data:', error);
    throw error;
  }
},

  async createTest(formData: CampaignData): Promise<void> {
    try {
      console.log(`Creating campaign: ${formData.campaignId}`);
      const response = await fetch(`http://localhost:3000/api/v1/create_campaign`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),

      });

      if (!response.ok) {
        throw new Error('Failed to create test');
      }
    } catch (error) {
      console.error('Error creating test:', error);
      throw error;
    }
  },

};

export default ABTestService;
