import React, { useState } from 'react';
import CreateTest from './components/CreateTest/CreateTest';
import TestResults from './components/TestResults/TestResults';
import RunTest from './components/RunTest/RunTest';
import TestsList from './components/TestList/TestList';

const App: React.FC = () => {
  const [test_name, settest_name] = useState<string | null>(null);
  const [currentTab, setCurrentTab] = useState('testsList'); // Set the default tab to 'testsList'

  const handleTestSelection = (selectedTestName: string) => {
    settest_name(selectedTestName);
    setCurrentTab('testResults');
  };

  return (
    <div className="app">
      <header>
        <h1><span>Win</span><span>Ab</span></h1>
        <p>Discover what your customers respond to and maximize your impact with WinAb</p>
      </header>
      <nav>
        <div 
          className={`tab ${currentTab === 'createTest' ? 'active' : ''}`}
          onClick={() => setCurrentTab('createTest')}
        >
          Create AB Test
        </div>
        <div 
          className={`tab ${currentTab === 'runTest' ? 'active' : ''}`}
          onClick={() => setCurrentTab('runTest')}
        >
          Create Mock Clicks/Impressions
        </div>
        <div 
          className={`tab ${currentTab === 'testsList' ? 'active' : ''}`}
          onClick={() => setCurrentTab('testsList')}
        >
          View All Tests
        </div>
        <div 
          className={`tab ${currentTab === 'testResults' ? 'active' : ''}`}
          onClick={() => setCurrentTab('testResults')}
        >
          AB Test Results
        </div>
      </nav>
      <div className="tab-content">
        {currentTab === 'createTest' && <CreateTest setTestId={settest_name} />}
        {currentTab === 'runTest' && <RunTest />}
        {currentTab === 'testsList' && <TestsList onTestSelect={handleTestSelection} />}
        {currentTab === 'testResults' && test_name && <TestResults test_name={test_name} />}
      </div>
    </div>
  );
};

export default App;
