// ABTestService.ts
import { TestResultResponse, ABTestFormData } from './types';

const ABTestService = {
  async fetchData(test: string): Promise<TestResultResponse> {
    try {
      const response = await fetch(`http://localhost:5002/calculate_results?test_name=${testName}`);
      console.log(response);
      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error fetching data:', error);
      throw error;
    }
  },

  async createTest(formData: ABTestFormData): Promise<void> {
    try {
      const response = await fetch(`http://localhost:5001/create_experiment`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Failed to create test');
      }
    } catch (error) {
      console.error('Error creating test:', error);
      throw error;
    }
  },


// create a method to get all the tests 
async getExperimentIds(): Promise<string[]> {
  try {
    const response = await fetch(`http://localhost:5000/get_experiments`);
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error fetching data:', error);
    throw error;
  }
},



};



export default ABTestService;
