// ABTestService.ts
import { TestResultResponse, ABTestFormData } from './types';

const ABTestService = {
 // Replace TestResultResponse with ResultData in the return type of getTestResults
async getTestResults(experiment_id: number): Promise<TestResultResponse> {
  try {
    const response = await fetch(`http://localhost:5002/calculate_test_results?test_name=${experiment_id}`);
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error fetching data:', error);
    throw error;
  }
},

  async createTest(formData: ABTestFormData): Promise<void> {
    try {
      const response = await fetch(`http://localhost:5000/v1/create_experiment`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Failed to create test');
      }
    } catch (error) {
      console.error('Error creating test:', error);
      throw error;
    }
  },


// create a method to get all the tests 
async getExperimentIds(): Promise<string[]> {
  try {
    const response = await fetch(`http://localhost:5000/v1/get_experiment_ids`);
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error fetching data:', error);
    throw error;
  }
},



};



export default ABTestService;
