import React, { useState } from 'react';
import './App.css';
import CreateTest from './components/CreateTest/CreateTest';
import RunTest from './components/RunTest/RunTest';
import TestResult from './components/TestResult/TestResult';
import TestResults from './components/TestResults/TestResults';

const App: React.FC = () => {
  const [testName, setTestName] = useState<string | null>(null);
  const [currentTab, setCurrentTab] = useState('createTest');

  const handleTestSelection = (selectedTestName: string) => {
    setTestName(selectedTestName);
    setCurrentTab('testResult');
  };

  return (
    <div className="app">
      <header>
        <h1>
          <span>Win</span>
          <span>Ab</span>
        </h1>
        <p>Discover what your customers respond to and maximize your impact with WinAb</p>
      </header>

      <nav>
        <div
          className={`tab ${currentTab === 'createTest' ? 'active' : ''}`}
          onClick={() => setCurrentTab('createTest')}
        >
          Create AB Test
        </div>
        <div
          className={`tab ${currentTab === 'runTest' ? 'active' : ''}`}
          onClick={() => setCurrentTab('runTest')}
        >
          Run Test
        </div>
        <div
          className={`tab ${currentTab === 'testResults' ? 'active' : ''}`}
          onClick={() => setCurrentTab('testResults')}
        >
          Test Results
        </div>
        <div
          className={`tab ${currentTab === 'testResult' ? 'active' : ''}`}
          onClick={() => setCurrentTab('testResult')}
        >
          Test Detail
        </div>
      </nav>

      <div className="tab-content">
        {currentTab === 'createTest' && <CreateTest setTestId={setTestName} />}
        {currentTab === 'runTest' && <RunTest />}
        {currentTab === 'testResults' && <TestResults onTestSelect={handleTestSelection} />}
        {currentTab === 'testResult' && testName && <TestResult test_name={testName} />}
      </div>
    </div>
  );
};

export default App;
