import React, { useState } from 'react';
import ABTestService from '../../services/api';
import { makeStyles } from '@material-ui/core/styles';
import { Button, TextField, Container, Box, Typography } from '@material-ui/core';

interface CreateTestProps {
  setTestId: React.Dispatch<React.SetStateAction<string | null>>;
}

const CreateTest: React.FC<CreateTestProps> = ({ setTestId }) => {
  const [formData, setFormData] = useState({
    name: '',
    start_date: '',
    variants: [
      {
        name: 'A',
        elements: [
          { element_type: 'Headline', xpath: '' },
          { element_type: 'CTA', xpath: '' }
        ]
      }
    ]
  });

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = event.target;
    if (event.target.tagName === 'INPUT') {
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    } else if (event.target.tagName === 'TEXTAREA') {
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    }
  };
  
  

  const handleVariantChange = (event: React.ChangeEvent<HTMLInputElement>, index: number) => {
    const { name, value } = event.target;
    const updatedVariants = [...formData.variants];
    updatedVariants[index] = {
      ...updatedVariants[index],
      [name]: value
    };
    setFormData((prevData) => ({
      ...prevData,
      variants: updatedVariants
    }));
  };

  const handleElementChange = (
    event: React.ChangeEvent<HTMLInputElement>,
    variantIndex: number,
    elementIndex: number
  ) => {
    const { name, value } = event.target;
    const updatedVariants = [...formData.variants];
    const updatedElements = [...updatedVariants[variantIndex].elements];
    updatedElements[elementIndex] = {
      ...updatedElements[elementIndex],
      [name]: value
    };
    updatedVariants[variantIndex] = {
      ...updatedVariants[variantIndex],
      elements: updatedElements
    };
    setFormData((prevData) => ({
      ...prevData,
      variants: updatedVariants
    }));
  };

  const handleAddVariant = () => {
    const variantName = formData.variants.length > 0 ? 'B' : 'A';
    const newVariant = {
      name: variantName,
      elements: [
        { element_type: 'Headline', xpath: '' },
        { element_type: 'CTA', xpath: '' }
      ]
    };
    setFormData((prevData) => ({
      ...prevData,
      variants: [...prevData.variants, newVariant]
    }));
  };

  const handleAddElement = (variantIndex: number) => {
    const newElement = { element_type: '', xpath: '' };
    const updatedVariants = [...formData.variants];
    updatedVariants[variantIndex].elements.push(newElement);
    setFormData((prevData) => ({
      ...prevData,
      variants: updatedVariants
    }));
  };

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    try {
      await ABTestService.createTest(formData);
      setTestId(formData.name);
      alert('Test created successfully');
    } catch (error) {
      console.error('Error creating test:', error);
      alert('Failed to create test');
    }
  };

  const useStyles = makeStyles((theme) => ({
    field: {
      marginTop: theme.spacing(1)
    }
  }));

  const classes = useStyles();

  return (
    <Container maxWidth="sm">
            <form noValidate autoComplete="off" onSubmit={handleSubmit}>
        <Box mb={3}>
          <TextField
            id="name"
            label="Experiment Name"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            variant="outlined"
            fullWidth
            required
          />

          {formData.variants.map((variant, variantIndex) => (
            <Box key={variantIndex} mb={3}>
              <Typography variant="h6" component="h2">
                Variant {variantIndex + 1}
              </Typography>

              <TextField
                id={`variant-name-${variantIndex}`}
                label="Variant Name"
                name={`variant-name-${variantIndex}`}
                value={variant.name}
                onChange={(event) => handleVariantChange(event, variantIndex)}
                variant="outlined"
                fullWidth
                required
              />

              {variant.elements.map((element, elementIndex) => (
                <Box key={elementIndex} mt={2}>
                  <Typography variant="subtitle1" component="h3">
                    Element {elementIndex + 1}
                  </Typography>

                  <TextField
                    id={`element-type-${variantIndex}-${elementIndex}`}
                    label="Element Type"
                    name={`element-type-${variantIndex}-${elementIndex}`}
                    value={element.element_type}
                    onChange={(event) => handleElementChange(event, variantIndex, elementIndex)}
                    variant="outlined"
                    fullWidth
                  />

                  <TextField
                    id={`element-xpath-${variantIndex}-${elementIndex}`}
                    label="XPath"
                    name={`element-xpath-${variantIndex}-${elementIndex}`}
                    value={element.xpath}
                    onChange={(event) => handleElementChange(event, variantIndex, elementIndex)}
                    variant="outlined"
                    fullWidth
                  />
                </Box>
              ))}

              <Button
                variant="outlined"
                color="primary"
                onClick={() => handleAddElement(variantIndex)}
                className={classes.field}
              >
                Add Element
              </Button>
            </Box>
          ))}

          <Button
            variant="outlined"
            color="primary"
            onClick={handleAddVariant}
            className={classes.field}
          >
            Add Variant
          </Button>
        </Box>

        <Button variant="contained" color="primary" type="submit">
          Create Test
        </Button>
      </form>
    </Container>
  );
};

export default CreateTest;
