import React, { useState } from 'react';
import ABTestService from '../../services/api';
import { styled } from '@mui/material/styles';
import { Button, TextField, Container, Box, Typography } from "@mui/material";

interface CreateTestProps {
  setTestId: React.Dispatch<React.SetStateAction<string | null>>;
}

const FormContainer = styled('form')({
  '& .MuiTextField-root': {
    margin: '8px',
    width: '25ch',
  },
});

const LineInput = styled(TextField)({
  '& .MuiInputBase-root': {
    border: 'none',
    borderBottom: '1px solid rgba(0, 0, 0, 0.42)',
  },
});

const CreateTest: React.FC<CreateTestProps> = ({ setTestId }) => {
  const [campaignData, setCampaignData] = useState({
    campaign_name: '',
    control_name: '',
    control_identifier: '',
    type: '',
    variants: [
      {
        name: '',
        identifier: '',
        id: Math.random(),
      } 
    ]
  });

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setCampaignData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleVariantChange = (event: React.ChangeEvent<HTMLInputElement>, index: number) => {
    const { name, value } = event.target;
    console.log('Index:', index);
    console.log('Name:', name);
    console.log('Value:', value);
    const updatedVariants = [...campaignData.variants];
    updatedVariants[index] = {
      ...updatedVariants[index],
      [name]: value
    };
    setCampaignData((prevData) => ({
      ...prevData,
      variants: updatedVariants
    }));
  };

  const handleAddVariant = () => {
    const newVariant = {
      name: '',
      x_path: '',
      total_clicks: 0,
      ctr: 0,
      id: Math.random(),
    };
    setCampaignData((prevData) => ({
      ...prevData,
      variants: [...prevData.variants, newVariant]
    }));
  };

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    try {
      await ABTestService.createTest(campaignData);
      setTestId(campaignData.campaign_name);
      alert('Test created successfully');
    } catch (error) {
      console.error('Error creating test:', error);
      alert('Failed to create test');
    }
  };

  return (
    <Container maxWidth="sm">
      <FormContainer noValidate autoComplete="off" onSubmit={handleSubmit}>
        <Box mb={3}>
          <Typography variant="h6" component="h2">
            Set Up Campaign
          </Typography>

          <LineInput
            id="campaign_name"
            label="Campaign Name"
            name="campaign_name"
            value={campaignData.campaign_name}
            onChange={handleInputChange}
            variant="outlined"
            fullWidth
            required
          />

<LineInput
  id="control_name"
  label="Original Name"
  name="control_name"
  value={campaignData.control_name}
  onChange={handleInputChange}
  variant="outlined"
  fullWidth
  required
/>

<LineInput
  id="control_identifier"
  label="Original Xpath"
  name="control_identifier"
  value={campaignData.control_identifier}
  onChange={handleInputChange}
  variant="outlined"
  fullWidth
  required
/>

<TextField
  id="type"
  select
  label="Type of Test"
  name="type"
  value={campaignData.type}
  onChange={handleInputChange}
  SelectProps={{ native: true }}
  variant="outlined"
  fullWidth
>
  <option value="">Select</option>
  <option value="headline">Headline</option>
  <option value="teaser image">Teaser Image</option>
  <option value="custom">Custom</option>
</TextField>

      

          <Typography variant="h6" component="h2" sx={{ mt: 2 }}>
            Create Variants
          </Typography>

          {campaignData.variants.map((variant, variantIndex) => (
            <Box key={variantIndex} mb={3}>
              <TextField
                id={`variant-name-${variantIndex}`}
                label="Variant Name"
                name="name"
                value={variant.name}
                onChange={(event: React.ChangeEvent<HTMLInputElement>) =>
                  handleVariantChange(event, variantIndex)
                }
                variant="outlined"
                fullWidth
                required
              />

              <LineInput
                id={`variant-xpath-${variantIndex}`}
                label="Variant x_path"
                name="x_path"
                value={variant.x_path}
                onChange={(event: React.ChangeEvent<HTMLInputElement>) =>
                  handleVariantChange(event, variantIndex)
                }
                variant="outlined"
                fullWidth
              />
            </Box>
          ))}

          <Button
            variant="outlined"
            color="primary"
            onClick={handleAddVariant}
            sx={{ mt: 2 }}
          >
            Add Variant
          </Button>
        </Box>

        <Button variant="contained" color="primary" type="submit">
          Create Test
        </Button>
      </FormContainer>
    </Container>
  );
};

export default CreateTest;

function uuidv4(): any {
  throw new Error('Function not implemented.');
}
