import React, { useState } from 'react';
import './App.css';
import CreateTest from './components/CreateTest/CreateTest';
import TestResults from './components/TestResults/TestResults';
import RunTest from './components/RunTest/RunTest'; // import the RunTest component

const App: React.FC = () => {

  // add a global variable to store the test_id
  const [test_id, setTest_id] = useState<string | null>(null);

  // Add a state variable for the current tab
  const [currentTab, setCurrentTab] = useState('createTest');

  return (
    <div className="app">
      <header>
        <h1>WinAb</h1>
        <p>Discover what your customers respond to and maximize your impact with WinAb</p>
      </header>
      <nav>
        <button onClick={() => setCurrentTab('createTest')}>Create Test</button>
        <button onClick={() => setCurrentTab('testResults')}>Test Results</button>
        <button onClick={() => setCurrentTab('runTest')}>Run Test</button>
      </nav>
      <div className="tab">
        {currentTab === 'createTest' && <CreateTest setTestId={setTest_id} />}
        {currentTab === 'testResults' && <TestResults test_id={test_id} />}
        {currentTab === 'runTest' && <RunTest />}
      </div>
    </div>
  );
};

export default App;
