import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { TextField, Button, makeStyles, Typography, Select, MenuItem, InputLabel, FormControl } from '@material-ui/core';

const RunTest: React.FC = () => {
  const [testNames, setTestNames] = useState<string[]>([]); // Provide an initial value of empty array  const [testName, setTestName] = useState("");
  const [click, setClick] = useState(0);
  const [impressions, setImpressions] = useState(0);
  const [variant_segment, setVariantSegment] = useState(0);
  const [control_x_path, setControlXPath] = useState("");
  const [variant_x_path, setVariantXPath] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    fetchTestNames();
  }, []);

  const fetchTestNames = async () => {
    try {
      const response = await axios.get('http://localhost:5000/test_names');
      setTestNames(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleSelectChange = (
    event: React.ChangeEvent<{ value: unknown }>
  ) => {
    const value = event.target.value;
    if (typeof value === "string") {
      setTestName(value);
    }
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
  
    const payload = {
      test_name: testName, 
      click, 
      impressions, 
      variant_segment, 
      control_x_path, 
      variant_x_path
    };
    try {
      const response = await axios.post('http://localhost:5001/ab-test-metric-input', payload);
      console.log(response.data);
      setError(""); // clear error message on successful submission
    } catch (error) {
      console.error(error);
    }
  };  

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <FormControl required fullWidth>
          <InputLabel>Test Name</InputLabel>
          <Select
    value={testName}
    onChange={handleSelectChange}
  >
    {testNames.map((name, index) => (
      <MenuItem value={name} key={index}>{name}</MenuItem>
    ))}
  </Select>
        </FormControl>
        {error && <Typography color="error">{error}</Typography>}
   
        {error && <Typography color="error">{error}</Typography>}
        <TextField
          label="Clicks"
          type="number"
          value={click}
          onChange={e => setClick(Number(e.target.value))}
          required
          fullWidth
        />
        <TextField
          label="Impressions"
          type="number"
          value={impressions}
          onChange={e => setImpressions(Number(e.target.value))}
          required
          fullWidth
        />
        <TextField
          label="Variant Segment"
          type="number"
          value={variant_segment}
          onChange={e => setVariantSegment(Number(e.target.value))}
          required
          fullWidth
        />
        <TextField
          label="Control XPath"
          value={control_x_path}
          onChange={e => setControlXPath(e.target.value)}
          required
          fullWidth
        />
        <TextField
          label="Variant XPath"
          value={variant_x_path}
          onChange={e => setVariantXPath(e.target.value)}
          required
          fullWidth
        />
        <Button type="submit" variant="contained" color="primary">
          Run Test
        </Button>
      </form>
    </div>
  );
};

export default RunTest;
